import sys

def createHash(s, w, pows, ords):
    h = 0
    for i in range(w):
        h += (ords[s[i]] * pows[w-i-1]) % 1000003
    return h % 1000003

def updateHash(h, c1, c2, w, pows, ords):
    return (h*31 - pows[w]*ords[c1] + ords[c2]) % 1000003

def solve(line, pows, ords):
    w = 1
    res = ''
    while (True):
        dic = dict()
        h = createHash(line, w, pows, ords)
        dic[h] = 1
        for i in range(1, len(line) - w + 1):
            h = updateHash(h, line[i-1], line[i+w-1], w, pows, ords)
            if h not in dic:
                dic[h] = 1
            else:
                dic[h] += 1
        maxi = max(dic.values())
        if maxi > 1:
            res += str(maxi) + '\n'
            w += 1
        else:
            break
    return res

